import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { map } from 'rxjs/operators';
import { HttpClient } from '@angular/common/http';

// CONFIG
import { environment } from '../../../environments/environment';

// MODEL
import { Teacher } from '../../domain/sms_db/teacher';

/**
 * THIS SERVICE MAKE HTTP REQUEST TO SERVER, FOR CUSTOMIZE IT EDIT ../Teacher.service.ts
 */

/*
 * SCHEMA DB Teacher
 *
	{
		FirstName: {
			type: 'String',
			required : true
		},
		LastName: {
			type: 'String',
			required : true
		},
		MiddleName: {
			type: 'String'
		},
		//RELATIONS
		//EXTERNAL RELATIONS
		_Student: {
			type: Schema.ObjectId,
			ref : "Teacher"
		},
		_Teacher: {
			type: Schema.ObjectId,
			ref : "Student"
		},
		_Teacher: {
			type: Schema.ObjectId,
			ref : "User"
		},
	}
 *
 */
@Injectable()
export class TeacherBaseService {

    contextUrl: string = environment.endpoint + '/teacher';
    constructor(
        protected http: HttpClient
        ) { }

    // CRUD METHODS

    /**
    * TeacherService.create
    *   @description CRUD ACTION create
    *
    */
    create(item: Teacher): Observable<Teacher> {
        return this.http
            .post<Teacher>(this.contextUrl, item)
            .pipe(map(data => data));
    }

    /**
    * TeacherService.delete
    *   @description CRUD ACTION delete
    *
    */
    remove(id: string): Observable<void> {
        return this.http
            .delete<void>(this.contextUrl + '/' + id)
            .pipe(map(data => data));
    }

    /**
    * TeacherService.findByFirstName
    *   @description CRUD ACTION findByFirstName
    *
    */
    findByFirstName(id: string): Observable<Teacher[]> {
        return this.http
            .get<Teacher[]>(this.contextUrl + '/findByFirstName/' + id)
            .pipe(
                map(response => response)
            );
    }

    /**
    * TeacherService.findBy_Student
    *   @description CRUD ACTION findBy_Student
    *
    */
    findBy_Student(id: string): Observable<Teacher[]> {
        return this.http
            .get<Teacher[]>(this.contextUrl + '/findBy_Student/' + id)
            .pipe(
                map(response => response)
            );
    }

    /**
    * TeacherService.get_Student
    *   @description CRUD ACTION get_Student
    *
    */
    get_Student(id: string): Observable<any[]> {
        return this.http
            .get<any[]>(this.contextUrl + '/' + id + 'get_Student')
            .pipe(
                map(response => response)
            );
     }

    /**
    * TeacherService.update
    *   @description CRUD ACTION update
    *   @returns Teacher
    *
    */
    update(item: Teacher): Observable<Teacher> {
        return this.http
            .post<Teacher>(this.contextUrl + '/' + item._id, item)
            .pipe(map(data => data));
    }


    // Custom APIs

}
