 // DEPENDENCIES
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { map } from 'rxjs/operators';
import { HttpClient } from '@angular/common/http';

// CONFIG
import { environment } from '../../../environments/environment';

// MODEL
import { User } from '../../domain/sms_db/user';

/**
 * THIS SERVICE MAKE HTTP REQUEST TO SERVER, FOR CUSTOMIZE IT EDIT ../User.service.ts
 */

/*
 * SCHEMA DB User
 *
	{
		mail: {
			type: 'String'
		},
		name: {
			type: 'String'
		},
		password: {
			type: 'String',
			required : true
		},
		roles: {
			type: 'String'
		},
		surname: {
			type: 'String'
		},
		username: {
			type: 'String',
			required : true
		},
		//RELATIONS
		//EXTERNAL RELATIONS
		_Student: {
			type: Schema.ObjectId,
			ref : "User"
		},
		_Teacher: {
			type: Schema.ObjectId,
			ref : "User"
		},
	}
 *
 */
@Injectable()
export class UserBaseService {

    contextUrl: string = environment.endpoint + '/user';
    constructor(
        protected http: HttpClient
        ) { }

    // CRUD METHODS

    /**
    * UserService.create
    *   @description CRUD ACTION create
    *
    */
    create(item: User): Observable<User> {
        return this.http
            .post<User>(this.contextUrl, item)
            .pipe(map(data => data));
    }

    /**
    * UserService.delete
    *   @description CRUD ACTION delete
    *   @param ObjectId id Id
    *
    */
    remove(id: string): Observable<void> {
        return this.http
            .delete<void>(this.contextUrl + '/' + id)
            .pipe(map(data => data));
    }

    /**
    * UserService.findBy_Student
    *   @description CRUD ACTION findBy_Student
    *
    */
    findBy_Student(id: string): Observable<User[]> {
        return this.http
            .get<User[]>(this.contextUrl + '/findBy_Student/' + id)
            .pipe(
                map(response => response)
            );
    }

    /**
    * UserService.findBy_Teacher
    *   @description CRUD ACTION findBy_Teacher
    *
    */
    findBy_Teacher(id: string): Observable<User[]> {
        return this.http
            .get<User[]>(this.contextUrl + '/findBy_Teacher/' + id)
            .pipe(
                map(response => response)
            );
    }

    /**
    * UserService.findByname
    *   @description CRUD ACTION findByname
    *
    */
    findByName(id: string): Observable<User[]> {
        return this.http
            .get<User[]>(this.contextUrl + '/findByname/' + id)
            .pipe(
                map(response => response)
            );
    }

    /**
    * UserService.get
    *   @description CRUD ACTION get
    *   @param ObjectId id Id resource
    *
    */
    get(id: string): Observable<User> {
        return this.http
            .get<User>(this.contextUrl + '/' + id)
            .pipe(map(data => data));
    }

    /**
    * UserService.get_Student
    *   @description CRUD ACTION get_Student
    *
    */
    get_Student(id: string): Observable<any[]> {
        return this.http
            .get<any[]>(this.contextUrl + '/' + id + 'get_Student')
            .pipe(
                map(response => response)
            );
     }

    /**
    * UserService.get_Teacher
    *   @description CRUD ACTION get_Teacher
    *
    */
    get_Teacher(id: string): Observable<any[]> {
        return this.http
            .get<any[]>(this.contextUrl + '/' + id + 'get_Teacher')
            .pipe(
                map(response => response)
            );
     }

    /**
    * UserService.list
    *   @description CRUD ACTION list
    *
    */
    list(): Observable<User[]> {
        return this.http
            .get<User[]>(this.contextUrl)
            .pipe(map(data => data));
    }

    /**
    * UserService.update
    *   @description CRUD ACTION update
    *   @param ObjectId id Id
    *
    */
    update(item: User): Observable<User> {
        return this.http
            .post<User>(this.contextUrl + '/' + item._id, item)
            .pipe(map(data => data));
    }


    // Custom APIs


    /**
    * UserService.changePassword
    *   @description Change password of user from admin
    *   @returns object
    *
    */
    changePassword(...params: any[]): Observable<any> {
        return this.http
            .post<any>(this.contextUrl + '/{id}/changePassword', {})
            .pipe(
                map(response => response)
            );
    }

}
